import React from 'react'
import './'

function Avatar (props) {
  return (
    <div className="avatar">
      <img src={props.user.url} alt={props.user.name}/>
      <br/>
      <span>{props.user.name}</span>
    </div>
  )
}

function App () {
  let user = {
    url: "https://www.google.com.br/google.jpg",
    name: "Ayrton Guerra"
  }

  return(
    <>
      <Avatar user={user}/>
    </>
  ) 
}

export default App
